css:
.salary-summary-header {
    text-align: center;
    margin-bottom: 30px;
    border-bottom: 2px solid #333;
    padding-bottom: 15px;
}

.salary-details-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 20px;
}

.salary-details-table th {
    background-color: #f8f9fa;
    border: 1px solid #dee2e6;
    padding: 12px;
    text-align: left;
    font-weight: bold;
}

.salary-details-table td {
    border: 1px solid #dee2e6;
    padding: 10px 12px;
}

.completed-yes {
    color: #28a745;
    font-weight: bold;
}

.completed-no {
    color: #dc3545;
    font-weight: bold;
}

.total-row {
    background-color: #e7f3ff;
    font-weight: bold;
}

.company-logo {
    max-width: 150px;
    max-height: 80px;
}

.report-footer {
    margin-top: 40px;
    padding-top: 20px;
    border-top: 1px solid #333;
    font-size: 12px;
    color: #666;
}
